import { Injectable } from '@angular/core';
import {Observable} from "rxjs";
import {AuthenticationRequest, AuthenticationResponse, AuthenticationService} from "../../../journal-api/src";
import {HttpClient, HttpHeaders} from "@angular/common/http";
import jwtDecode from "jwt-decode";

@Injectable({
  providedIn: 'root'
})
export class UserService {

    token!:string ;
    isAuthenticated!:boolean ;
    email!:string ;

    constructor(private authenticationService:AuthenticationService , private http:HttpClient
    ) {
    }

    login(authenticationRequest:AuthenticationRequest){

      let apiUrl:string = 'http://localhost:8083/auth/login';
      const headers = new HttpHeaders({ 'Content-Type': 'application/json' });
      return this.http.post(apiUrl, authenticationRequest, { headers, responseType: 'json' });

    }

  setAccessToken(authenticationResponse: any): void {
    // localStorage.setItem('accessToken', JSON.stringify(authenticationResponse));
      this.isAuthenticated = true ;
      this.token = authenticationResponse['accessToken'] ;
      let decodedJwt= jwtDecode(this.token) ;
      this.email = decodedJwt.email ;
  }

}
