import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders, HttpErrorResponse } from '@angular/common/http';
import {map, Observable, throwError} from 'rxjs';
import { catchError } from 'rxjs/operators';
import jwtDecode from "jwt-decode";

@Injectable({
    providedIn: 'root'
})
export class AuthService {


    isAuthenticated:boolean = false ;
    roles:any  ;
    email:any ;
    accessToken!:string ;


    private apiUrl = 'http://localhost:8083/auth/login'; // Replace with your API endpoint
    constructor(private http: HttpClient) { }

    // Example of a POST request with a request body
    login(email: string, password: string): Observable<any> {
        const loginData = { email, password };
        const headers = new HttpHeaders({
            'Content-Type': 'application/json'
        });

      return this.http.post(`${this.apiUrl}`, loginData, { headers }).pipe(
        catchError(this.handleError),
        map((response: any) => {
          if (typeof response === 'string') {
            // If the response is a string, it means an error occurred (e.g., "Invalid email or password").
            throw new Error(response);
          } else {
            // If the response is not a string, it should be the token.
            return response;
          }
        })
      );
    }

    private handleError(error: HttpErrorResponse) {
        if (error.error instanceof ErrorEvent) {
            // A client-side or network error occurred
            console.error('An error occurred:', error.error.message);
        } else {
            // The backend returned an unsuccessful response code
            console.error(
                `Backend returned code ${error.status}, ` +
                `body was: ${error.error}`
            );
        }
        // Return an observable with a user-facing error message
        return throwError('Something went wrong. Please try again later.');
    }

    loadProfile(value: any) {
        this.accessToken = value ;
        let decodedJwt:any = jwtDecode(this.accessToken) ;
        this.email = decodedJwt.sub ;
        this.roles = decodedJwt.scope ;

        }
}
