import { Injectable } from '@angular/core';
import {AuthenticationService} from "../../../journal-api/src/services/authentication.service";
import {AuthenticationRequest} from "../../../journal-api/src/models/authentication-request";
import {Authenticate$Params} from "../../../journal-api/src/fn/authentication/authenticate";
import {Observable} from "rxjs";
import {AuthenticationResponse} from "../../../journal-api/src/models/authentication-response";

@Injectable({
  providedIn: 'root'
})
export class UserService {

    constructor(private authenticationService:AuthenticationService
    ) {
    }

    login(authenticationRequest:AuthenticationRequest):Observable<AuthenticationResponse>{
        const params: Authenticate$Params = {
            body: authenticationRequest // Map the request body
        };
        return this.authenticationService.authenticate(params) ;
    }

    setConnectedUser(authenticationResponse:AuthenticationResponse){
        let token:string = JSON.stringify(authenticationResponse) ;
        console.log(token) ;
        localStorage.setItem('connectedUser', JSON.stringify(authenticationResponse));
    }

}
