import { Injectable } from '@angular/core';
import {Observable} from "rxjs";
import {AuthenticationRequest, AuthenticationResponse, AuthenticationService} from "../../../journal-api/src";
import {HttpClient} from "@angular/common/http";

@Injectable({
  providedIn: 'root'
})
export class UserService {

    constructor(private authenticationService:AuthenticationService , private http:HttpClient
    ) {
    }

    login(authenticationRequest:AuthenticationRequest){

      const requestBody = {
        email: authenticationRequest.email,
        password: authenticationRequest.password,
      };

      // Define the HTTP headers to specify "application/json" content type
      const headers = new HttpHeaders({ 'Content-Type': 'application/json' });

      // Make the POST request with the request body and headers
      return this.http.post(this.apiUrl, requestBody, { headers });
    }

  setAccessToken(authenticationResponse: AuthenticationResponse): void {
    localStorage.setItem('accessToken', JSON.stringify(authenticationResponse));
    console.log(localStorage.getItem('accessToken')) ;

  }

}
